class CustomRandom {
    constructor(seed) {
        this.m = 0x80000000; // 2^31
        this.a = 1664525;
        this.c = 1013904223;
        this.state = seed ? seed : new Date().getTime(); // Use current timestamp as default seed if none provided
    }

    random() {
        this.state = (this.a * this.state + this.c) % this.m;
        return ((this.state / this.m) * 10) | 0;
    }
}

module.exports = CustomRandom;
